const setDropdown=e=>{const t=document.querySelector(e);if(t){const e=t.querySelector(".dropdown__textbox"),o=t.querySelectorAll(".dropdown__option");e.value=o[0].getAttribute("data-option"),t.addEventListener("click",()=>{t.classList.toggle("dropdown--active")});const i=t=>{e.value=t};o.forEach(e=>{e.addEventListener("click",()=>i(e.getAttribute("data-option")))})}};setDropdown(".dropdown--nav"),setDropdown(".dropdown--links");const setSlidePopup=e=>{const t=document.querySelector(e);if(t){t.querySelectorAll(".links__item").forEach(e=>{const t=e.querySelector(".links__visits");if(t){const o=e.querySelector(".links__open");o.addEventListener("click",()=>{t.classList.contains("links__visits--active")?(t.classList.remove("links__visits--active"),o.classList.remove("links__open--active"),t.style.height="0px"):(t.classList.add("links__visits--active"),o.classList.add("links__open--active"),t.style.height=t.querySelector(".links__inner").offsetHeight+"px")})}})}};setSlidePopup(".links--slidable");const clearSearchValue=()=>{const e=document.querySelector(".form__search"),t=document.querySelector(".form__remove");e&&t.addEventListener("click",()=>{e.value=""})};clearSearchValue();const swiper=new Swiper(".swiper",{loop:!0,pagination:{el:".swiper-pagination",clickable:!0},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});